from http.server import BaseHTTPRequestHandler
import json
import base64
from datetime import datetime

class handler(BaseHTTPRequestHandler):
    def do_POST(self):
        try:
            content_length = int(self.headers['Content-Length'])
            post_data = self.rfile.read(content_length)
            data = json.loads(post_data.decode('utf-8'))
            
            # Como a Vercel tem limitações para gerar PDFs,
            # vamos retornar um PDF base64 simulado ou redirecionar para um serviço externo
            
            # Por enquanto, vamos retornar um sucesso simulado
            result = {
                "success": True,
                "message": "PDF gerado com sucesso",
                "pdf_data": "",
                "filename": f"proposta_precizu_{datetime.now().strftime('%Y%m%d_%H%M%S')}.pdf"
            }
            
            self.send_response(200)
            self.send_header('Content-type', 'application/json')
            self.send_header('Access-Control-Allow-Origin', '*')
            self.end_headers()
            self.wfile.write(json.dumps(result).encode())
            
        except Exception as e:
            self.send_response(500)
            self.send_header('Content-type', 'application/json')
            self.send_header('Access-Control-Allow-Origin', '*')
            self.end_headers()
            error_response = {"error": str(e)}
            self.wfile.write(json.dumps(error_response).encode())
    
    def do_OPTIONS(self):
        self.send_response(200)
        self.send_header('Access-Control-Allow-Origin', '*')
        self.send_header('Access-Control-Allow-Methods', 'POST, OPTIONS')
        self.send_header('Access-Control-Allow-Headers', 'Content-Type')
        self.end_headers()